/*!50003 CREATE*/ /*!50017 DEFINER=`avnadmin`@`%`*/ /*!50003 TRIGGER `trg_before_update_client_stats` BEFORE INSERT ON `Operations` FOR EACH ROW BEGIN
  DECLARE cnt INT DEFAULT 0;

  -- Si es venta, actualiza total_purchases y fidelidad
  IF LOWER(NEW.operation_type) = 'sale' THEN
    
    SELECT COUNT(*) INTO cnt
      FROM Operations
     WHERE fk_client_id_operation = NEW.fk_client_id_operation
       AND LOWER(operation_type)   = 'sale';
    
    SET cnt = cnt + 1;

    UPDATE Clients
       SET total_purchases = cnt,
           fidelity_level  = CASE
                              WHEN cnt BETWEEN 3 AND 5 THEN 'frecuente'
                              WHEN cnt >= 6             THEN 'aficionado'
                              ELSE fidelity_level
                             END
     WHERE id_client = NEW.fk_client_id_operation;
  END IF;

  -- Si es renta, actualiza total_rentals
  IF LOWER(NEW.operation_type) = 'rent' THEN
    
    SELECT COUNT(*) INTO cnt
      FROM Operations
     WHERE fk_client_id_operation = NEW.fk_client_id_operation
       AND LOWER(operation_type)   = 'rent';
    
    SET cnt = cnt + 1;

    UPDATE Clients
       SET total_rentals = cnt
     WHERE id_client = NEW.fk_client_id_operation;
  END IF;
END */;;
