/*!50003 CREATE*/ /*!50017 DEFINER=`avnadmin`@`%`*/ /*!50003 TRIGGER `trg_after_op_update_stock` AFTER INSERT ON `Operations` FOR EACH ROW BEGIN
  -- Si es una renta, descuenta 1 unidad de stock de renta solo si hay stock
  IF LOWER(NEW.operation_type) = 'rent' THEN
    UPDATE Products
       SET rent_stock = rent_stock - 1
     WHERE product_id = NEW.fk_product_id_operation
       AND rent_stock > 0;

  -- Si es una venta, descuenta 1 unidad de stock de venta solo si hay stock
  ELSEIF LOWER(NEW.operation_type) = 'sale' THEN
    UPDATE Products
       SET sale_stock = sale_stock - 1
     WHERE product_id = NEW.fk_product_id_operation
       AND sale_stock > 0;
  END IF;
END */;;
